function getRandomNumber(r,t,e=!0){if("number"!=typeof r||"number"!=typeof t)throw new Error("Both min and max must be numbers");if(t<=r)throw new Error("Min must be less than max");t=Math.random()*(t-r)+r;return e?Math.floor(t):t}function getRandomString(t){if("number"!=typeof t||t<=0)throw new Error("Length must be a positive number");var e="ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";let n="";for(let r=0;r<t;r++){var o=Math.floor(Math.random()*e.length);n+=e[o]}return n}export{getRandomNumber,getRandomString};